mapper.insert:

insert into `order` (clientId, total_price, createTime, state
  )
values (#{clientid,jdbcType=BIGINT}, #{totalPrice,jdbcType=DECIMAL}, #{createtime,jdbcType=TIMESTAMP}, #{state,jdbcType=VARCHAR}
  )

mapper.update:

update `order`
set clientId = #{clientid,jdbcType=BIGINT},
  total_price = #{totalPrice,jdbcType=DECIMAL},
  createTime = #{createtime,jdbcType=TIMESTAMP},
  state = #{state,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
    select 
    aa.*, 
    cc.name as "clientName",
    cc.address as "clientAddress" from `order` aa

    left join client cc on aa.clientid = cc.id


		and aa.id = #{id}


		and aa.clientId = #{clientid}


		and aa.total_price = #{totalPrice}


		and TO_DAYS(aa.createTime) = TO_DAYS(#{createtime})


		and aa.state = #{state}


    order by id desc

SELECT o.orderid AS "orderId", oi.dish_id AS "dishId", d.`name` AS "dishName", oi.quantity AS "quantity", o.clientid AS "clientId", o.`name` AS "clientName", o.address AS "address", o.createTime AS "createtime", #{startTime} AS "startTime", #{endTime} AS "endTime" FROM order_item oi LEFT JOIN dish d ON d.id = oi.dish_id, (SELECT oin.id AS orderid, oin.createTime AS createTime, c.id AS clientid, c.`name` AS `name`, c.address AS address FROM `order` oin LEFT JOIN client c ON oin.clientId = c.id
and oin.createTime >= #{startTime}
and oin.createTime <= #{endTime}
) o WHERE oi.order_id = o.orderid
and oi.dish_id = #{dishId}
